                $sql = $db->sql_build_query('SELECT', $sql_array);

                $result = $db->sql_query($sql);

                $wl_access_token = $db->sql_fetchfield('session_wl_access_token');

                $db->sql_freeresult($result);

                if($wl_access_token)
